#! usr/bin/env python3


# this is the logo
# its in a list because that allows me to collapse it in the VS Code IDE.
LOGO_BASE64 = [
    b""
]


# imports
import os
import sys
import time
import string
import random
import turtle
from base64 import decodebytes

# install packages
try:
    from tqdm import tqdm
    from colorama import Fore
except ModuleNotFoundError:
    os.system(f"{sys.executable} -m pip install colorama tqdm")


# global data
clear = lambda : os.system("cls" if os.name == "nt" else "clear")


def say_hello():
    """
    Prints a welcome message. Requires no parameters and always returns
    0.
    """
    clear()
    message = "Welcome to the GreenLine Self Serve Checkout."
    print(Fore.GREEN, message)
    return 0


def gen_advert():
    """
    Generates the advert `.gif` file.

    Takes no parameters.

    ### Returns

    `int` - 0.
    """
    # generate a random name
    # this will prevent namespace collisions with a 
    # high degree of reliability

    # create sample space
    characters_list = [i for i in string.ascii_lowercase]

    # shuffle, add to list, repeat
    random.shuffle(characters_list)
    tmp:list = []

    for i in range(100):
        tmp.append(random.choice(characters_list))
        random.shuffle(characters_list)

    # declare name
    global GENERATED_NAME
    GENERATED_NAME =  f".{''.join(tmp)}.gif"



    # write the contents to file
    gif_contents = decodebytes(LOGO_BASE64[0])
    with open(GENERATED_NAME, "wb") as file:
        file.write(gif_contents)

    return 0


def display_advert():
    """
    Displays the logo to the screen.
    """
    turtle.bgpic(GENERATED_NAME)
    turtle.bgcolor("grey")
    pass


def clean_up():
    """
    Deletes the logo generated by the program at runtime.
    """
    # guard clauses
    if GENERATED_NAME is None: return 0
    elif os.path.exists(GENERATED_NAME) is False: return 0

    # remove
    os.remove(GENERATED_NAME)
    return 0


def calc(prices:list[int|float], discount_percentage:int = 0) -> tuple[float]:
    """
    Calculates the prices of the purchase.

    ### Parameters

    `discount` - The full percentage discount, as an integer. 
    DO NOT PROVIDE AS A `0.x` DECIMAL NUMBER. The discount is
    implicitly converted into a percentage.

    ### Returns

    `tuple[float]` - The final price, GST, discount percentage, and the total of the purchase, as floats and in that order.
    """
    # sum prices
    x = sum(prices)
    # apply discount
    x = x - (x*(discount_percentage/100))
    # apply gst
    return (round(x + (x*0.05), 2), round(x*0.05, 2), round(x*(discount_percentage/100), 2), sum(prices))


def system():
    """
    Intializes files and boots system.
    """
    clear()
    
    for i in tqdm(range(100), "Booting", colour="green"):
        time.sleep(0.1 * random.random())


    # declare needed variables
    items_list = []
    cost_list = []
    # declare a filter list
    filter_list = [i for i in string.ascii_letters + string.punctuation]
    filter_list.remove(".")

    # event loop
    exit_loop = False
    while not exit_loop:
        # display hello message
        say_hello()
        
        # formatting current list
        for i in items_list:
            print(Fore.GREEN, i)
        # tell user what to do
        print(Fore.GREEN, "Please scan an item or enter Ctrl+D (Ctrl+Z on Windows) to proceed to payment calculation.")
        
        try:

            # get and format item
            new_item = input(" ")
            items_list.append(new_item)
            new_item = new_item.replace(",", ".")
            for letter in filter_list:
                new_item = new_item.replace(letter, "")
            
            # handling decimals in front of cost + spaces
            num_list = [i for i in string.digits]

            detected = False
            for i in num_list:
                if i in new_item:
                    detected = True
            
            if not detected: continue

            while new_item[0:1] not in num_list:
                new_item = new_item[1:]

            # append item
            cost_list.append(float(new_item))

        except ValueError:
            print(Fore.GREEN, "Invalid item entered.")
            print(Fore.GREEN, "Press Enter to continue")
            input()
            continue

        except EOFError:
            exit_loop = True
        
    clear()
    print("Proceeding with Checkout.")
    # calculate and show user.
    print("Your order is:")
    for i in items_list:
        print(i)

    # format end prices
    total_cost, gst, discount, gross_total = calc(cost_list, random.choice([10, 20, 30]))
    print("Your purchase before tax and discounts is: ${:.2f}".format(gross_total))
    print("Your discount for today is: ${:.2f}".format(discount))
    print("Your GST tax amount is: ${:.2f}".format(gst))
    print("Your Total cost is: ${:.2f}".format(total_cost))

    print("Please press Enter to exit the program.")
    # wait for user to end program
    input()
    clear()
    exit(0)